import React from 'react'
import {removeDestinationAction} from '../redux/action'
import {connect} from 'react-redux'
import Destination from './Destination'

class DestinationsList extends React.Component {

    


    renderDestinations = () => {
        return this.props.destinations.map(destination => {
            return <Destination destination={destination} callDispatch={callDispatch} />
        })
    }

    // callDispatch = (destination) => {
    //     console.log(destination)
    //    this.destination.removeDestination(destination)
    // }

    render() {
        return (
            <div className="list-container">
                   {this.renderDestinations()}
            </div>
        )
    }
}

const callDispatch = destination => {
    this.props.removeDestination(destination)
}

const mapStateToProps = state => {
    return {destinations: state.destinations}
}


const mapDispatchToProps = dispatch => {
    return {
      removeDestination: destination => dispatch(removeDestinationAction(destination))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(DestinationsList)
