import React from 'react'
import {connect} from 'react-redux'
import {addDestinationAction} from '../redux/action'

class Form extends React.Component {

    state = {
        title: ""
    }

    handleTitleChange = e => {
        this.setState({[e.target.title]: e.target.value})
    }

    handleSubmit = e => {
        e.preventDefault()
        ////const payload = {...this.state}
        //console.log(payload)
        this.props.addDestination(this.state)
        // payload is newDes
        this.setState({title: ''})
    }

  
    render() {
        return(
            <form onSubmit={this.handleSubmit}>
              
                <input type="text"
                  name="name"
                  onChange={this.handleTitleChange}
                  value={this.state.title}
                  placeholder= "title" />
              

              <input type="submit"
                value={"Add"}
              />
            </form>

        )
    }
}

//dispatch to the reducer, map state from

const mapStateToProps = (state) => {
    const {destinations} = state
    return {destinations}
  }
const mapDispatchToProps = dispatch => {
    return {
        addDestination: newDes => dispatch(addDestinationAction(newDes))
    }
}



export default connect(mapStateToProps, mapDispatchToProps)(Form)
