import React from 'react'
import {removeDestinationAction} from '../redux/action'
import {connect} from 'react-redux'

class Destination extends React.Component {

    render() {
        return (
            <div className="destination">
            "Destination View"
          <p onClick={() => this.props.removeDestination(destination)}>{destination.city}, {destination.country} <br></br>Dates: {destination.date} <br></br>Activities: {destination.activities} </p>
        </div>

        
    )
}
}

const mapDispatchToProps = dispatch => {
    return {
      removeDestination: destination => dispatch(removeDestinationAction(destination))
    }
}

export default connect(null, mapDispatchToProps)(Destination)
