import React from 'react'
import {connect} from 'react-redux'
import {setDestinationAction, addDestinationAction, removeDestinationAction} from '../redux/action'

class Destinations extends React.Component {

    renderDestinations = () => {
        return this.props.destinations.map(destination => (
          <p onClick={() => this.props.removeDestination(destination)}>Title: {destination.title}</p>
        ))
      }

    render() {
        return (
            <div>
                Places I've Traveled To
                {console.log(this.props.destinations)}
                {this.renderDestinations()}
           
            </div>
        )
    }

    
    fetchDestinations = () => {
        fetch('http://localhost:3001/destinations')
            .then(res => res.json())
            .then(des => this.props.setDestinations()
            )
    }

    componentDidMount() {
        this.fetchDestinations()
    }
}

const mapStateToProps = state => {
    return {destinations: state.destinations}
}

const mapDispatchToProps = dispatch => {
    return {
        setDestination: destinations => dispatch(setDestinationAction(destinations)),
        addDestination: destination =>  dispatch(addDestinationAction(destination)),
        removeDestination: destination => dispatch(removeDestinationAction(destination))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Destinations)
