import React from 'react'
import { connect } from 'react-redux'
import { addStudentAction } from './redux/actions'

class StudentForm extends React.Component {

  state = {
    title: '',
    age: '',
    grade: 'A'
  }

  handleChange = event => {
    this.setState({[event.target.title]: event.target.value})
  }

  handleSubmit = event => {
    event.preventDefault()
    this.props.addStudent(this.state)
    this.setState({title: '', age: '', grade: 'A'})
  }

  render() {
    return (

      <form id="student-form" onSubmit={this.handleSubmit}>

        <input type="text"
        title="title"
        onChange={this.handleChange}
        value={this.state.title}
        placeholder='title'
        />

        <input type="submit"
        value="Add Student"
        />

      </form>

    )
  }

}

const mapStateToProps = (state) => {
  const {students} = state
  return {students}
}

const mapDispatchToProps = dispatch => {
  return {
    addStudent: (newStudent) => dispatch(addStudentAction(newStudent))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(StudentForm)
