import React from 'react'
import { connect } from 'react-redux'
import {addDestinationAction} from '../redux/action'

class Form extends React.Component {

  state = {
    title: '',
  }

  handleChange = event => {
    this.setState({[event.target.title]: event.target.value})
  }

  handleSubmit = event => {
    event.preventDefault()
    this.props.adddestinations(this.state)
    this.setState({title: ''})
  }

  render() {
    return (

      <form id="destinations-form" onSubmit={this.handleSubmit}>

        <input type="text"
        title="city"
        onChange={this.handleChange}
        value={this.state.city}
        placeholder='city'
        />

        <input type="text"
        title="country"
        onChange={this.handleChange}
        value={this.state.country}
        placeholder='country'
        />

        <input type="text"
        title="date"
        onChange={this.handleChange}
        value={this.state.date}
        placeholder='date'
        />
        
        <input type="textarea"
        title="activities"
        onChange={this.handleChange}
        value={this.state.activities}
        placeholder='title'
        />

        <input type="submit"
        value="Add"
        />

      </form>

    )
  }

}

//dispatch sends info to the reducer

const mapStateToProps = (state) => {
  const {destinations} = state
  return {destinations}
}

const mapDispatchToProps = dispatch => {
  return {
    adddestinations: (newDestinations) => dispatch(addDestinationAction(newDestinations))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Form)
