export function fetchDestinations() {
    return (dispatch) => {
    fetch('http://localhost:3001/destinations')
        .then(res => res.json())
        .then(destinations => {
            this.props.setDestinations(destinations)
            console.log(destinations)
            }
        )
  }
}

export const setDestinationAction = destinations => {
  console.log("action")
  return {
   
    type: "SET_DESTINATION",
    payload: destinations
  }
}

export const addDestinationAction = newDestination => {
  return {
    type: "ADD_DESTINATION",
    payload: newDestination
  }
}

export const removeDestinationAction = destination => {
  return {
    type: "REMOVE_DESTINATION",
    payload: destination
  }
}
