const defaultState = {
    destinations: []
}

function reducer(state = defaultState, action) {
    switch (action.type) {
        case "ADD_DESTINATION":
            return {...state, destinations: [...state.destinations, action.payload]}
        default: 
            return state
            //when dispatching we are overriding current state
        case "ADD_DESTINATION":
            return {...state, destinations: [...state.destinations, action.payload]}
       
    }
}

export default reducer
